using namespace std;

int main()
{
    ios::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);

    vector<int> arr;
    long long sum = INT_MIN;
    long long current = 0;
    int start, end;
    int temp;

    while(cin >> temp) arr.push_back(temp);
    for(int i = 0; i < arr.size(); i++){
    	current += arr[i];

    	if(current < 0){
    	    current = 0;
    	    start = i+1;
    	}else if(current > sum){
    		sum = current;
    		end = i;
    	}
    }

    cout << "Max is: " << sum << endl;
    cout << "Range: " << start << ',' << end << endl;
    return 0;
}